#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\sikar\Desktop\Hill\Project\Weather App\node_modules\loose-envify\node_modules;C:\Users\sikar\Desktop\Hill\Project\Weather App\node_modules;C:\Users\sikar\Desktop\Hill\Project\node_modules;C:\Users\sikar\Desktop\Hill\node_modules;C:\Users\sikar\Desktop\node_modules;C:\Users\sikar\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\sikar\Desktop\Hill\Project\Weather App\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/sikar/Desktop/Hill/Project/Weather App/node_modules/loose-envify/node_modules:/mnt/c/Users/sikar/Desktop/Hill/Project/Weather App/node_modules:/mnt/c/Users/sikar/Desktop/Hill/Project/node_modules:/mnt/c/Users/sikar/Desktop/Hill/node_modules:/mnt/c/Users/sikar/Desktop/node_modules:/mnt/c/Users/sikar/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/sikar/Desktop/Hill/Project/Weather App/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../loose-envify/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../loose-envify/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../loose-envify/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../loose-envify/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
